import React, { useState } from "react";
import RegisterCustomer from "./components/RegisterCustomer";
import CustomerList from "./components/CustomerList";
import CustomerDetail from "./components/CustomerDetail";

const App = () => {
  const [customers, setCustomers] = useState([]);

  const handleRegister = (formData) => {
    // Add a new customer with a unique ID
    const newCustomer = { ...formData, id: Date.now().toString() };
    setCustomers((prevCustomers) => [...prevCustomers, newCustomer]);
  };

  const handleDelete = (id) => {
    // Filter out the customer with the given id
    setCustomers((prevCustomers) =>
      prevCustomers.filter((customer) => customer.id !== id)
    );
  };

  return (
    <div>
      <RegisterCustomer onRegister={handleRegister} />
      <CustomerList
        customers={customers}
        onViewCustomer={(customer) => console.log(customer)} // Replace with your logic to view customer details
        onDeleteCustomer={handleDelete}
      />
      {/* Optionally, you can add logic to view customer details */}
      {/* <CustomerDetail customer={selectedCustomer} /> */}
    </div>
  );
};

export default App;
