import React, { useState } from "react";
import axios from "axios";

const RegisterCustomer = ({ onRegister }) => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    gender: "",
    occupation: "",
    contact: "",
    hobby: "",
    salary: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/customer", formData);
      const newCustomerID = response.data.clientID;
      // Call the onRegister function with the newly registered customer ID
      onRegister(newCustomerID);
      // Clear the form after registration
      setFormData({
        name: "",
        age: "",
        gender: "",
        occupation: "",
        contact: "",
        hobby: "",
        salary: "",
      });
    } catch (error) {
      console.error("Error registering customer:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Your form elements go here */}
      {/* Example: */}
      <input
        type="text"
        name="name"
        value={formData.name}
        onChange={handleChange}
        placeholder="Name"
      />
      <input
        type="text"
        name="age"
        value={formData.age}
        onChange={handleChange}
        placeholder="Age"
      />
      <input
        type="text"
        name="gender"
        value={formData.gender}
        onChange={handleChange}
        placeholder="Gender"
      />
      <input
        type="text"
        name="occupation"
        value={formData.occupation}
        onChange={handleChange}
        placeholder="Occupation"
      />
      <input
        type="text"
        name="contact"
        value={formData.contact}
        onChange={handleChange}
        placeholder="Contact"
      />
      <input
        type="text"
        name="hobby"
        value={formData.hobby}
        onChange={handleChange}
        placeholder="Hobby"
      />
      <input
        type="text"
        name="salary"
        value={formData.salary}
        onChange={handleChange}
        placeholder="Salary"
      />
      <button type="submit">Register</button>
    </form>
  );
};

export default RegisterCustomer;
