import React, { useState, useEffect } from "react";
import axios from "axios";

const CustomerList = ({ onViewCustomer, onDeleteCustomer }) => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    // Fetch the list of registered customers on component mount
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get("http://localhost:3000/customer/1f99b138-3e35-4d16-8bd2-91f70d3114ac");
      console.log(1)
      console.log(response.data)
      response.data.forEach((e, value) => {
        setCustomers({e, value})
      })
      console.log(customers)
      console.log(1)
    } catch (error) {
      console.error("Error fetching customers:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/customer/${id}`);
      onDeleteCustomer(id);
    } catch (error) {
      console.error("Error deleting customer:", error);
    }
  };

  return (
    <div>
      <h2>Customer List</h2>
      <ul>
        {customers}
        {/* {customers.map((customer) => (
          <li key={customer.id}>
            {customer.name}{" "}
            <button onClick={() => onViewCustomer(customer)}>View</button>{" "}
            <button onClick={() => handleDelete(customer.id)}>Delete</button>
          </li>
        ))} */}
      </ul>
    </div>
  );
};

export default CustomerList;
