import React, { useState, useEffect } from 'react';
import axios from 'axios';
const baseURL = "http://localhost:3000"

const CustomerList = ({ onSelectCustomer }) => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    axios.get(`${baseURL}/api/customers`)
      .then(response => setCustomers(response.data))
      .catch(error => console.log(error));
  }, []);

  const fetchCustomers = () => {
    axios.get(`${baseURL}/api/customers`)
      .then(response => setCustomers(response.data))
      .catch(error => console.log(error));
  };

  const handleDeleteCustomer = (customerId) => {
    axios.delete(`${baseURL}/api/customers/${customerId}`)
      .then(response => {
        // Update the customer list after deletion
        fetchCustomers();
        setSelectedCustomer(null);
      })
      .catch(error => console.log(error));
  };

  return (
    <div>
      <h2>Customer List</h2>
      <ul>
        {customers.map(customer => (
          <li key={customer.id} onClick={() => onSelectCustomer(customer)}>
            {customer.name}{' '}
            <button onClick={() => handleDeleteCustomer(customer.id)}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CustomerList;
