import React, { useState } from "react";
import axios from 'axios';
import api from "./api";

const RegisterCustomer = ({ onRegister }) => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    gender: "",
    occupation: "",
    contact: "",
    hobby: "",
    salary: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    console.log(1)
    e.preventDefault();
    
    try {
      let id;
      await axios.post("http://localhost:3000/customer", [formData], 'application/json').then(() => {
        console.log(1023)
      })
      console.log(2)
      // Call the onRegister function with the newly registered customer ID
      onRegister(id);
      // Clear the form after registration
      setFormData({
        name: "",
        age: "",
        gender: "",
        occupation: "",
        contact: "",
        hobby: "",
        salary: "",
      });
    } catch (error) {
      console.error("Error registering customer:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={formData.name}
        onChange={handleChange}
        placeholder="Name"
      />
      <input
        type="text"
        name="age"
        value={formData.age}
        onChange={handleChange}
        placeholder="Age"
      />
      <input
        type="text"
        name="gender"
        value={formData.gender}
        onChange={handleChange}
        placeholder="Gender"
      />
      <input
        type="text"
        name="occupation"
        value={formData.occupation}
        onChange={handleChange}
        placeholder="Occupation"
      />
      <input
        type="text"
        name="contact"
        value={formData.contact}
        onChange={handleChange}
        placeholder="Contact"
      />
      <input
        type="text"
        name="hobby"
        value={formData.hobby}
        onChange={handleChange}
        placeholder="Hobby"
      />
      <input
        type="text"
        name="salary"
        value={formData.salary}
        onChange={handleChange}
        placeholder="Salary"
      />
      <input
        type="button"
        class="button"
        name="Register"
        value="Register"
        onClick={handleSubmit}
      />
    </form>
  );
};

export default RegisterCustomer;
