import React, { useState } from 'react';
import CustomerList from './components/CustomerList';
import CustomerDetail from './components/CustomerDetail';
import RegisterCustomer from './components/RegisterCustomer'; // Import the new component
import axios from 'axios';

const baseURL = "http://localhost:3000"

const App = () => {
  const [selectedCustomer, setSelectedCustomer] = useState(null);

  const handleSelectCustomer = (customer) => {
    setSelectedCustomer(customer);
  };

  const fetchCustomers = () => {
    axios.get(`${baseURL}/api/customers`)
      .then(response => setCustomers(response.data))
      .catch(error => console.log(error));
  };

  const handleDeleteCustomer = (customerId) => {
    axios.delete(`${baseURL}/api/customers/${customerId}`)
      .then(response => {
        // Update the customer list after deletion
        fetchCustomers();
        setSelectedCustomer(null);
      })
      .catch(error => console.log(error));
  };

  const handleRegisterCustomer = (newCustomer) => {
    axios.post(`${baseURL}/api/customers`, newCustomer)
      .then(response => {
        // Update the customer list after successful registration
        setSelectedCustomer(response.data);
      })
      .catch(error => console.log(error));
  };

  return (
    <div>
      <h1>Customer Management App</h1>
      <div className="customer-management-container">
        <div className="customer-list-container">
          <CustomerList onSelectCustomer={handleSelectCustomer} />
        </div>
        <div className="customer-details-container">
          <CustomerDetail customer={selectedCustomer} />
        </div>
        <div className="register-customer-container">
          <RegisterCustomer onRegister={handleRegisterCustomer} />
        </div>
      </div>
      {selectedCustomer && (
        <button onClick={() => handleDeleteCustomer(selectedCustomer.id)}>
          Delete Customer
        </button>
      )}
    </div>
  );
};

export default App;
