import React, { useState, useEffect } from "react";
import axios from "axios";
import RegisterCustomer from "./components/RegisterCustomer";
import CustomerList from "./components/CustomerList";
import CustomerDetail from "./components/CustomerDetail";

const App = () => {
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const handleRegister = (newCustomerID) => {
    fetchCustomers();
  };

  const handleViewCustomer = (customer) => {
    setSelectedCustomer(customer);
  };

  const handleDeleteCustomer = (id) => {
    if (selectedCustomer && selectedCustomer.id === id) {
      setSelectedCustomer(null);
    }
    fetchCustomers();
  };

  const fetchCustomers = async () => {
    try {
      const response = await axios.get("/customer-list");
      setCustomers(response.data);
    } catch (error) {
      console.error("Error fetching customers:", error);
    }
  };

  return (
    <div>
      <RegisterCustomer onRegister={handleRegister} />
      <CustomerList
        customers={customers} // Pass the list of customers to the CustomerList component
        onViewCustomer={handleViewCustomer}
        onDeleteCustomer={handleDeleteCustomer}
      />
      {selectedCustomer && <CustomerDetail customer={selectedCustomer} />}
    </div>
  );
};

export default App;
