import React, { useState, useEffect } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

const API_BASE_URL = "http://localhost:3000";

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    axios.get(`${API_BASE_URL}/customer`).then((response) => {
      setCustomers(response.data);
    });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`${API_BASE_URL}/customer`, { data: { id } }).then(() => {
      setCustomers((prevCustomers) => prevCustomers.filter((c) => c.clientID !== id));
    });
  };

  return (
    <div>
      <h2>Customer List</h2>
      <ul>
        {customers.map((customer) => (
          <li key={customer.clientID}>
            <Link to={`/customer/${customer.clientID}`}>{customer.name}</Link>{" "}
            <button onClick={() => handleDelete(customer.clientID)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

const CustomerDetail = ({ match }) => {
  const [customer, setCustomer] = useState(null);

  useEffect(() => {
    const customerId = match.params.id;
    axios.get(`${API_BASE_URL}/customer/${customerId}`).then((response) => {
      setCustomer(response.data);
    });
  }, [match.params.id]);

  if (!customer) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Customer Detail</h2>
      <p>Name: {customer.name}</p>
      <p>Age: {customer.age}</p>
      <p>Gender: {customer.gender}</p>
      <p>Occupation: {customer.occupation}</p>
      <p>Contact: {customer.contact}</p>
      <p>Hobby: {customer.hobby}</p>
      <p>Salary: {customer.salary}</p>
    </div>
  );
};

const CustomerManagementApp = () => {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/customer">Customer List</Link>
            </li>
          </ul>
        </nav>

        <hr />

        <Route path="/" exact render={() => <h1>Welcome to Customer Management</h1>} />
        <Route path="/customer" exact component={CustomerList} />
        <Route path="/customer/:id" component={CustomerDetail} />
      </div>
    </Router>
  );
};

export default CustomerManagementApp;
